document.querySelector('.magnetic-button').addEventListener('mousemove', function(e) {
  const rect = this.getBoundingClientRect();
  const x = e.clientX - rect.left;
  const y = e.clientY - rect.top;
  this.style.setProperty('--x', x + 'px');
  this.style.setProperty('--y', y + 'px');
});

css:
.hover-underline {
  position: relative;
  text-decoration: none;
  color: #fff;
  font-size: 1.2rem;
  padding: 10px 0;
}
.hover-underline::after {
  content: '';
  position: absolute;
  bottom: 0;
  left: 0;
  width: 0;
  height: 2px;
  background: linear-gradient(45deg, #667eea, #764ba2);
  transition: width 0.3s ease;
}
.hover-underline:hover::after {
  width: 100%;
}

.gradient-border {
  padding: 20px;
  background: white;
  border: 4px solid transparent;
  background: 
    linear-gradient(white, white) padding-box,
    linear-gradient(45deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4) border-box;
  border-radius: 8px;
  position: relative;
}

html, body {
  background-color: #0A2647;
  color: rgba(255, 255, 255, 0.9);
  font-family: 'Poppins', sans-serif;
  text-align: center;
  padding-top: 3em;
  margin: 0;
}

.container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 2rem;
  min-height: 100vh;
}

.shine, .chrome {
  font-size: 3.8em;
  font-weight: 700;
  margin: 0;
  background: #222 -webkit-gradient(linear, left top, right top, from(#222), to(#222), color-stop(0.5, #fff)) 0 0 no-repeat;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  color: rgba(255, 255, 255, 0.9);
  text-shadow: 0 0 15px rgba(255, 255, 255, 0.15);
  letter-spacing: 2px;
  animation: fadeIn 2s ease-in-out;
}

.shine {
  -webkit-background-size: 150px;
  -webkit-animation: shine 8s infinite linear;
}

.chrome {
  background-image: -webkit-linear-gradient(-40deg, transparent 0%, transparent 40%, #fff 50%, transparent 60%, transparent 100%);
  -webkit-background-size: 200px;
  -webkit-animation: shine 8s infinite linear;
}

.button {
  background: linear-gradient(135deg, #205295, #2C74B3);
  border: none;
  border-radius: 10px;
  padding: 0.8rem 2rem;
  color: #fff;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(44, 116, 179, 0.4);
}

.button:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(44, 116, 179, 0.6);
}

@-webkit-keyframes shine {
  0%, 10% {
    background-position: -1000px;
  }
  30% {
    background-position: top left;
  }
  70% {
    background-position: top right;
  }
  100% {
    background-position: 1000px;
  }
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

:root {
  --accent-color: #03e9f4;
  --white-color: #fff;
  --box-shadow: 0 0 5px #03e9f4, 0 0 25px #03e9f4, 0 0 50px #03e9f4, 0 0 100px #03e9f4;
  --body-font: 'Montserrat', sans-serif;
}

.neon-border-button {
  position: relative;
  display: inline-block;
  padding: 20px 30px;
  color: var(--accent-color);
  text-transform: uppercase;
  overflow: hidden;
  letter-spacing: 4px;
  transition: 0.5s;
  font-family: var(--body-font);
}

.neon-border-button span {
  position: absolute;
  display: block;
}

.neon-border-button span:nth-child(1) {
  top: 0;
  left: -100%;
  width: 100%;
  height: 2px;
  background: linear-gradient(90deg, transparent, var(--accent-color));
  animation: btn-anim1 1s linear infinite;
}

@keyframes btn-anim1 {
  0% { left: -100%; }
  50%, 100% { left: 100%; }
}

.neon-border-button span:nth-child(2) {
  top: -100%;
  right: 0;
  width: 2px;
  height: 100%;
  background: linear-gradient(180deg, transparent, var(--accent-color));
  animation: btn-anim2 1s linear infinite;
  animation-delay: 0.25s;
}

@keyframes btn-anim2 {
  0% { top: -100%; }
  50%, 100% { top: 100%; }
}

.neon-border-button span:nth-child(3) {
  bottom: 0;
  right: -100%;
  width: 100%;
  height: 2px;
  background: linear-gradient(270deg, transparent, var(--accent-color));
  animation: btn-anim3 1s linear infinite;
  animation-delay: 0.5s;
}

@keyframes btn-anim3 {
  0% { right: -100%; }
  50%, 100% { right: 100%; }
}

.neon-border-button span:nth-child(4) {
  bottom: -100%;
  left: 0;
  width: 2px;
  height: 100%;
  background: linear-gradient(360deg, transparent, var(--accent-color));
  animation: btn-anim4 1s linear infinite;
  animation-delay: 0.75s;
}

@keyframes btn-anim4 {
  0% { bottom: -100%; }
  50%, 100% { bottom: 100%; }
}

.neon-border-button:hover {
  background-color: var(--accent-color);
  color: var(--white-color);
  border-radius: 5px;
  box-shadow: var(--box-shadow);
}

.glow-pulse {
  background: linear-gradient(45deg, #00c6ff, #0072ff);
  color: #fff;
  padding: 12px 24px;
  border-radius: 8px;
  font-weight: bold;
  text-transform: uppercase;
  border: none;
  animation: pulse 2s infinite;
}
@keyframes pulse {
  0% { box-shadow: 0 0 0 0 rgba(0, 114, 255, 0.6); }
  70% { box-shadow: 0 0 0 20px rgba(0, 114, 255, 0); }
  100% { box-shadow: 0 0 0 0 rgba(0, 114, 255, 0); }
}

.liquid-loader {
  position: relative;
  width: 100px;
  height: 100px;
  background: linear-gradient(45deg, #ff00cc, #3333ff);
  border-radius: 50%;
  animation: morph 3s ease-in-out infinite;
}
@keyframes morph {
  0%, 100% { border-radius: 40% 60% 70% 30% / 40% 30% 70% 60%; }
  50% { border-radius: 70% 30% 50% 50% / 50% 60% 40% 50%; }
}

.cube {
  position: relative;
  width: 100px;
  height: 100px;
  transform-style: preserve-3d;
  animation: spinCube 6s infinite linear;
}
.cube div {
  position: absolute;
  width: 100px;
  height: 100px;
  background: rgba(0,150,255,0.6);
  border: 2px solid #00f;
}
.cube .front { transform: translateZ(50px); }
.cube .back { transform: rotateY(180deg) translateZ(50px); }
.cube .left { transform: rotateY(-90deg) translateZ(50px); }
.cube .right { transform: rotateY(90deg) translateZ(50px); }
.cube .top { transform: rotateX(90deg) translateZ(50px); }
.cube .bottom { transform: rotateX(-90deg) translateZ(50px); }
@keyframes spinCube { to { transform: rotateX(360deg) rotateY(360deg); } }

.loader {
  position: relative;
  width: 2.5em;
  height: 2.5em;
  transform: rotate(165deg);
  /* center inside preview area instead of using absolute positioning */
  margin: 0.75em auto;
}

.loader:before, .loader:after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  display: block;
  width: 0.5em;
  height: 0.5em;
  border-radius: 0.25em;
  transform: translate(-50%, -50%);
}

.loader:before {
  animation: before 2s infinite;
}

.loader:after {
  animation: after 2s infinite;
}

@keyframes before {
  0% {
    width: 0.5em;
    box-shadow: 1em -0.5em rgba(225, 20, 98, 0.75), -1em 0.5em rgba(111, 202, 220, 0.75);
  }
  35% {
    width: 2.5em;
    box-shadow: 0 -0.5em rgba(225, 20, 98, 0.75), 0 0.5em rgba(111, 202, 220, 0.75);
