.pulsing-dots {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 0.6em;
  /* center inside preview area without touching global html/body */
  margin: 0.75em auto;
}

.pulsing-dots div {
  width: 0.8em;
  height: 0.8em;
  background-color: #2C74B3;
  border-radius: 50%;
  animation: pulse 0.8s infinite ease-in-out;
}

.pulsing-dots div:nth-child(2) { animation-delay: 0.2s; }
.pulsing-dots div:nth-child(3) { animation-delay: 0.4s; }

@keyframes pulse {
  0%, 100% { opacity: 0.3; transform: scale(1); }
  50% { opacity: 1; transform: scale(1.3); }
}

.ring-loader {
  display: inline-flex;
  width: 64px;
  height: 64px;
  margin: 0.75em auto; /* center inside preview area without touching global html/body */
  justify-content: center;
  align-items: center;
}

.ring-loader:after {
  content: ' ';
  display: block;
  width: 46px;
  height: 46px;
  margin: 1px;
  border-radius: 50%;
  border: 5px solid #fff; /* changed to white */
  border-color: #fff transparent #fff transparent; /* white ring + transparent gaps */
  animation: ring-spin 1.2s linear infinite;
}

@keyframes ring-spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.dark-rider-card {
  width: 200px;
  height: 300px;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: flex-end;
  padding: 0;
  perspective: 2500px;
  margin: 20px auto;
  background: #191c29;
  border-radius: 10px;
}

.dark-rider-card .wrapper {
  transition: all 0.5s;
  position: absolute;
  width: 100%;
  height: 100%;
  z-index: 1;
  overflow: hidden;
  border-radius: 10px;
}

.dark-rider-card .cover-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.dark-rider-card:hover .wrapper {
  transform: perspective(900px) translateY(-5%) rotateX(25deg) translateZ(0);
  box-shadow: 2px 35px 32px -8px rgba(0, 0, 0, 0.75);
}

.dark-rider-card .wrapper::before,
.dark-rider-card .wrapper::after {
  content: '';
  opacity: 0;
  width: 100%;
  height: 80px;
  transition: all 0.5s;
  position: absolute;
  left: 0;
}

.dark-rider-card .wrapper::before {
  top: 0;
  height: 100%;
  background-image: linear-gradient(
    to top,
    transparent 46%,
    rgba(12, 13, 19, 0.5) 68%,
    rgba(12, 13, 19) 97%
